for charID in sorted(basePaletteColors.keys()):
	for i in range(36, 64):
		basePaletteColors[charID][i] = 0xFF000000;
	
	
def getPalSelColor(colors):
	# issue,, some colors aint even used, i,, could do a thing per char but thats not ideal
	res = colors[9]
	
	
	
	# i need to reverse this color, for stupid reasons, bc the palettes are in bgr and the paletteselect colors are in rgb?!
	
	r = (res & 0xFF) >> 0
	g = (res & 0xFF00) >> 8
	b = (res & 0xFF0000) >> 16
	
	res = 0xFF000000
	
	res |= (r << 16)
	res |= (g << 8)
	res |= (b << 0)
	
	return res
	
